import logo from './logo.svg';
import './App.css';
import {uesSate} from 'react';





function Article(props){
 return       <article>
 <h2>{props.title}</h2>
 {props.body}
</article> 
}

function Header(props){
  return <header>
  <h1><a href='/' onClick={(event)=>{
    event.preventDefault();
    props.onChangeMode();
  }}>{props.title}</a></h1>
</header>
}

function Nav(props){

  const lis = []
  for(let i = 0; i<props.topics.length; i++){    
    let t = props.topics[i];
    lis.push(<li key={t.id}>
      <a id= {t.id} onClick={(event)=>{
        event.preventDefault();
        props.onChangeMode(event.target.id);
        // event.target = event객체가 가지고 있는 target = 이벤트를 유발시키는 태그를 가리킨다 -> a태그
      }} href = {'/read/'+t.id}>{t.title}</a>
      </li>)
  }
  return       <nav>
  <ol>
    {lis}
  </ol>
</nav>
}



